<?phpclass dynamic_mod extends CI_Model{       public function __construct()    {        $this->load->database();    }		public function get_theme_details($local_admin_id){		$this->db->select('theme');	$this->db->from('app_local_admin_gen_setting');	$this->db->where('local_admin_id', $local_admin_id);	$LayoutThemeData = $this->db->get();	$DataArr = $LayoutThemeData->row();		$theme = $DataArr->theme;		if($theme == "CCS"){				$this->db->select('*');				$this->db->from('app_custom_color_scheme');				$this->db->where('local_admin_id', $local_admin_id);				$this->db->where('theme_name', $theme);				$LiqLayOutDataRows = $this->db->get();				return $LiqLayOutDataRows->row();		}else{				$this->db->select('*');				$this->db->from('app_color_scheme');				$this->db->where('theme_name', $theme);				$LiqLayOutDataRows = $this->db->get();				return $LiqLayOutDataRows->row();		}			}   }?>